programlistingco.programlisting:
/* FILE: simpleclient.c */
static const char *url = "http://csoap.sourceforge.net/cgi-bin/csoapserver";
static const char *urn = "urn:examples";
static const char *method = "sayHello";

int main(int argc, char *argv[])
{
  SoapEnv *env, *res;

   env = soap_env_new_with_method(urn, method);

   soap_env_add_item(env, "xsd:string", 
	"name", "Jonny B. Good");
   res = soap_client_invoke(env, url , "");

   soap_xml_doc_print(res->root->doc);
  
   soap_env_free(res);
   soap_env_free(env);

  return 0;
}

/* FILE: simpleserver.c */

int main(int argc, char *argv[])
{

  SoapRouter *router;
  
   if (!soap_server_init_args(argc, argv)) {
    return 0;
  }
  
   router = soap_router_new();

   soap_router_register_service(router, say_hello, "sayHello", "urn:examples");
   soap_server_register_router(router, "/csoapserver");

   soap_server_run();

   soap_router_free(router);
   soap_server_destroy();

  return 0;
}

section.synopsis:
typedef struct _SoapEnv
{ 
  xmlNodePtr root; 
  xmlNodePtr cur;
}SoapEnv;

xmlNodePtr 
soap_env_add_item(SoapEnv* env, const char *type, 
		  const char *name, const char *value);
xmlNodePtr 
soap_env_add_itemf(SoapEnv* env, const char *type, 
		  const char *name, const char *value, ...);
xmlNodePtr 
soap_env_push_item(SoapEnv *env, const char *type,
		   const char *name);
void
soap_env_pop_item(SoapEnv* env);

